#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\Workspace\pinny\node_modules\.pnpm\ts-jest@29.4.1_@babel+core@_3e1079b7e4b48c79cbef43f9fc1ea86f\node_modules\ts-jest\node_modules;D:\Workspace\pinny\node_modules\.pnpm\ts-jest@29.4.1_@babel+core@_3e1079b7e4b48c79cbef43f9fc1ea86f\node_modules;D:\Workspace\pinny\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/Workspace/pinny/node_modules/.pnpm/ts-jest@29.4.1_@babel+core@_3e1079b7e4b48c79cbef43f9fc1ea86f/node_modules/ts-jest/node_modules:/mnt/d/Workspace/pinny/node_modules/.pnpm/ts-jest@29.4.1_@babel+core@_3e1079b7e4b48c79cbef43f9fc1ea86f/node_modules:/mnt/d/Workspace/pinny/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../ts-jest/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../ts-jest/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../ts-jest/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../ts-jest/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
